import React, {useState} from "react"
import {connect} from 'react-redux'
import { authenticate, authSuccess, authFailure } from "../redux/authActions";
import { userLogin } from "../services/authService"
import { Form, Input, Button, Alert, Spin } from 'antd';



const LoginPage = ({loading, error, ...props}) => {
    const [values, setValues] = useState({
        username: '',
        password: ''
    });

    const handleSubmit = (e) => {
        // e.preventDefault();
        props.authenticate();

        userLogin(values).then((response) => {
            console.log("response:", response);
            
            if(response.status === 200) {
                props.setUser(response.data);
                props.history.push('/dashboard');
            }
            else {
                props.loginFailure("Something went wrong, Try again!")
            }
        }).catch((error) => {
            if(error && error.response) {
                if(error.response.status === 401) {
                    console.log("Status: 401");
                    props.loginFailure("Bad credentials, authentication failed !");
                }
                else {
                    props.loginFailure("Something went wrong, Try again!");
                }
            }
            else {
                props.loginFailure("Something went wrong, Try again!");
            }

        });
    }

    const handleChange = (e) => {
        e.persist();
        setValues(values => ({
        ...values,
        [e.target.name]: e.target.value
        }));
    };

    console.log("Loading ",loading);

//     const error = error ? <Row>
//     <Col span="8"></Col>
//     <Col span="8">
//       <Alert message="Authentication Failed" type="warning"></Alert>
//     </Col>
//   </Row> : ''

    return(
        <div className="form">
            <Form
        name="basic"
        onFinish={handleSubmit}
        labelCol={{
            span: 8,
            padding: 20,
        }}
        wrapperCol={{
            span: 16,
        }}
          initialValues={{
            remember: true,
          }}
        //   onFinish={onFinish}
        //   onFinishFailed={onFinishFailed}
          autoComplete="off"
        >
        <Form.Item
            label="Username"
            name="username"
            value={values.username}
            onChange={handleChange}
            rules={[
            {
                required: true,
                message: 'Please input your username!',
            },
            ]}
        >
            <Input />
        </Form.Item>

        <Form.Item
            label="Password"
            name="password"
            value={values.password}
            onChange={handleChange}
            rules={[
            {
                required: true,
                message: 'Please input your password!',
            },
            ]}
        >
            <Input.Password />
        </Form.Item>

        {/* <Form.Item
            name="remember"
            valuePropName="checked"
            wrapperCol={{
            offset: 8,
            span: 16,
            }}
        >
            <Checkbox>Remember me</Checkbox>    
        </Form.Item> */}

        <Form.Item
            wrapperCol={{
            offset: 8,
            span: 16,
            }}
        >
            <Button type="primary" htmlType="submit">
            Login
            {loading && (<Spin animation="border" aria-hidden="true"/>)}
            </Button>
        </Form.Item>

        { error && <Alert type="warning">{error}</Alert> }

        </Form>
    </div>
    )
    
}

const mapStateToProps=({auth})=>{
    console.log("state ",auth)
    return {
        loading:auth.loading,
        error:auth.error
}}


const mapDispatchToProps=(dispatch)=>{

    return {
        authenticate :()=> dispatch(authenticate()),
        setUser:(data)=> dispatch(authSuccess(data)),
        loginFailure:(message)=>dispatch(authFailure(message))
    }
}

export default connect(mapStateToProps,mapDispatchToProps)(LoginPage);
