// import React, { Fragment, useState } from "react"
import React, {useState} from "react"
// import { authenticate } from "react-redux";
import { userLogin } from "../services/authService"
import { Form, Input, Button, Alert } from 'antd';



const LoginPage = ({loading, error, ...props}) => {
    const [values, setValues] = useState({
        username: '',
        password: ''
    });

    const handleSubmit = (e) => {
        console.log("submit clicked");
        e.preventDefault();
        props.authenticate();

        console.log("submit clicked");

        userLogin(values).then((response) => {
            console.log("response:", response);
            
            if(response.status === 200) {
                props.setUser(response.data);
                props.history.push('/dashboard');
            }
            else {
                props.loginFailure("Something went wrong, Try again!")
            }
        }).catch((error) => {
            if(error && error.response) {
                if(error.response.status === 401) {
                    console.log("Status: 401");
                    props.loginFailure("Bad credentials, authentication failed !");
                }
                else {
                    props.loginFailure("Something went wrong, Try again!");
                }
            }
            else {
                props.loginFailure("Something went wrong, Try again!");
            }

        });
    }

    const handleChange = (e) => {
        e.persist();
        setValues(values => ({
        ...values,
        [e.target.name]: e.target.value
        }));
    };

    console.log("Loading ",loading);

//     const error = error ? <Row>
//     <Col span="8"></Col>
//     <Col span="8">
//       <Alert message="Authentication Failed" type="warning"></Alert>
//     </Col>
//   </Row> : ''

    return(
        <div className="form">
            <Form
        name="basic"
        labelCol={{
            span: 8,
            padding: 20,
        }}
        wrapperCol={{
            span: 16,
        }}
        //   initialValues={{
        //     remember: true,
        //   }}
        //   onFinish={onFinish}
        //   onFinishFailed={onFinishFailed}
        //   autoComplete="off"
        >
        <Form.Item
            label="Username"
            name="username"
            value={values.username}
            onChange={handleChange}
            rules={[
            {
                required: true,
                message: 'Please input your username!',
            },
            ]}
        >
            <Input />
        </Form.Item>

        <Form.Item
            label="Password"
            name="password"
            value={values.password}
            onChange={handleChange}
            rules={[
            {
                required: true,
                message: 'Please input your password!',
            },
            ]}
        >
            <Input.Password />
        </Form.Item>

        {/* <Form.Item
            name="remember"
            valuePropName="checked"
            wrapperCol={{
            offset: 8,
            span: 16,
            }}
        >
            <Checkbox>Remember me</Checkbox>    
        </Form.Item> */}

        <Form.Item
            wrapperCol={{
            offset: 8,
            span: 16,
            }}
        >
            <Button onSubmit={handleSubmit} type="primary" htmlType="submit">
            Submit
            </Button>
        </Form.Item>

        { error && <Alert type="warning">{error}</Alert> }

        </Form>
    </div>
    )
    
}



export default LoginPage;
